import React from 'react'
import Card from '../Components/Card'
import { useDentistStates } from '../Context'
import { Link } from 'react-router-dom'

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Home = () => {
  const {dentistState, dentistDispatch} = useDentistStates()
  const doctorImg= "../images/doctor.jpg"
  const addFav = ()=>{
    dentistDispatch({type: 'ADD_FAVS', payload: dentist})
    // Aqui iria la logica para agregar la Card en el localStorage
  }



  return (
    <main className="" >
      <h1>Home</h1>
      <div className='card-grid'>

      {dentistState.dentistList.map(dentist => <Link to= {"/dentist/" + dentist.id} key={dentist.id}>
          <Card             
            imgSrc= {doctorImg} 
            name={dentist.name} 
            username={dentist.username}

          />
          <button onClick={addFav} className="favButton">Add fav</button>
        </Link>)}
      </div>
    </main>
  )
}

export default Home
