import React from "react";
import { useDentistStates } from "../Context";
import { Link } from 'react-router-dom'

const Card = ({ imgSrc, name, username, id }) => {

  const {dentistDispatch, dentistState } = useDentistStates()

  const addFav = (event)=>{
    event.stopPropagation();
    dentistDispatch({type: 'ADD_FAVS', payload: dentistState.dentist.id})
    console.log(dentistState.favs);
    // Aqui iria la logica para agregar la Card en el localStorage
  }


  return (
    <div className="card">
        <img className="card" src={imgSrc} alt="doctor"/>
        <h3>{name}</h3>
        <h5>{username}</h5>

        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}

        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}
        <button onClick={addFav} className="favButton" ><i className="fas fa-star"></i></button>
        <Link to={`/dentist/${id}`}>View Details</Link> {/* This is the link to the details page */}

    </div>
  );
};

export default Card;
