import React from "react";
import Card from "../Components/Card";
import { useDentistStates } from "../Context";
import { useEffect } from "react";
import { useState } from "react";

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Favs = () => {

  const {dentistState, dentistDispatch} = useDentistStates()

  const doctorImg= "../images/doctor.jpg"

  const [objetos, setObjetos] = useState([]);

  const [favsAdded, setFavsAdded] = useState([]); // Nuevo estado para mantener los IDs de favoritos

  const theme = dentistState.theme === "dark" ? "dark" : "light";

  useEffect(() => {
    const misObjetos = localStorage.getItem('favs');
    if (misObjetos) {
      setObjetos(JSON.parse(misObjetos));
    }
  }, []);


  return (
    <main className={theme}> 
    <>
      <h1>Dentists Favs</h1>
      <div className="card-grid">

      {objetos && objetos.map(fav => <Card             
            imgSrc= {doctorImg} 
            name={fav.name} 
            username={fav.username}
            id={fav.id} // pass the id prop so the Card knows where to link to
            hideDelete = {false}
            isDisabled={true} // Pasamos la prop isDisabled

          />
      )}
        {/* este componente debe consumir los destacados del localStorage */}
        {/* Deberan renderizar una Card por cada uno de ellos */}
      </div>
    </>
    </main>
  );
};

export default Favs;
